mapper.delete:
/* 비정상 종료로 발생한 동일한 아이디에서 생성된 리프레시토큰을 삭제 */
 DELETE 
   FROM TOKEN_STORE
  WHERE id_num IN (
                    SELECT id_num
                      FROM (
                               SELECT id_num
                                 FROM TOKEN_STORE
                             GROUP BY id_num
                               HAVING COUNT(*) > 1
                            ) AS temp
                  );
