mapper.select:
/* 키워드 기반 팔로잉 목록 */
   SELECT nickname             /* 닉네임 */
        , profile              /* 프로필 이미지 */
     FROM MEMBER m
     JOIN FOLLOW f 
       ON m.id_num  =   f.following_id
    WHERE f.id_num  =   #{idNum}
      AND nickname LIKE CONCAT('%',#{keyword},'%') 
    LIMIT #{rowSize} OFFSET #{startRow}

   /* 유저피드 내 팔로잉,팔로워 리스트 정보 */
   SELECT nickname             /* 닉네임 */
        , profile              /* 프로필 */
        , approve
        , (
             SELECT approve 
               FROM FOLLOW
              WHERE id_num       = #{idNum}
                AND following_id = (  
                                      SELECT id_num
                                        FROM MEMBER 
                                       WHERE nickname = m.nickname
                                   )
          ) as myApprove            /* 현 회원과 매개변수의 회원의 팔로우 관계정보 서브쿼리 */
     FROM FOLLOW f
LEFT JOIN MEMBER m                  /* 회원 테이블 조인 */
    /* 팔로잉하는 사람들 */
       ON f.following_id = m.id_num
    WHERE f.id_num = (
                      SELECT id_num
                        FROM MEMBER
                       WHERE nickname = #{nickname}
                     )                          /* 팔로우 테이블 주체자가 매개변수로 받은 회원의 회원번호 */
      AND approve  =  'FOLLOWOK'                /* 팔로우 상태가 OK */

                 /* 팔로우 하는 사람 */
       ON f.id_num = m.id_num
    WHERE f.following_id = (
                            SELECT id_num
                              FROM MEMBER
                             WHERE nickname = #{nickname}	
                           )                      /* 팔로우 테이블 팔로우요청을받는 아이디가 매개변수로 받은 회원의 회원번호 */
      AND approve        = 'FOLLOWOK'             /* 팔로우 상태가 OK */

 ORDER BY follow_Date DESC
    LIMIT #{rowSize} OFFSET #{startRow}

mapper.insert:
/* 인서트 팔로잉 */
INSERT INTO FOLLOW	(id_num, following_id, approve)
VALUES (
        #{idNum}         /* 팔로잉 회원 고유번호 */
       ,(
           SELECT id_num
             FROM MEMBER
            WHERE nickname = #{nickname}
        )
       ,                 /* 팔로잉 받는 사람 회원 고유번호 */

              /* 비공개 계정일 시 팔로우 상태 */
         'FOLLOWNO'

            /* 공개 계정일 시 팔로우 상태 */
         'FOLLOWOK'


        )

mapper.update:
/*비공개 계정에 대한 팔로우 요청 수락*/
UPDATE FOLLOW SET
       approve = 'FOLLOWOK'      /* 팔로우 수락으로 상태 변경 */
 WHERE following_id=#{idNum}     /* 팔로우 받는 아이디 */
   AND id_num = (                /* 팔로우 하는 아이디 */
                  SELECT id_num
                    FROM MEMBER 
                   WHERE nickname = #{nickname}
                )

mapper.delete:
/* 팔로우 요청 거절 */
DELETE 
  FROM FOLLOW
 WHERE following_id = #{idNum}      /* 팔로우 받는 아이디 회원 고유번호 */
   AND id_num = (                   /* 팔로우 하는 아이디 회원 고유번호 */
                 SELECT id_num
                   FROM MEMBER 
                  WHERE nickname = #{nickname}
                )
