mapper.select:
/* 글로벌 서치 자동검색 결과 리스트 */ SELECT nickname as result /* 닉네임 */ , 'member' as type /* 타입(회원,관심사) */ , profile /* 프로필 사진 */ FROM MEMBER /* 멤버 테이블 */ WHERE 1=1 AND ( nickname LIKE CONCAT('%',#{keyword},'%') OR phone = #{keyword} /* 검색어 */ ) AND NOT id_num = #{idNum} /* 나 제외 */ AND locked !='FORBIDDEN' /* 정지계정 제외 */
UNION ALL
SELECT DISTINCT hobby as result /* 관심사 */ , 'hobby' as type /* 타입(회원,관심사) */ , null as profile /* 프로필 사진 */ FROM HOBBY /* 관심사 테이블 */ WHERE hobby LIKE CONCAT('%',#{keyword},'%') /* 검색어 */ ORDER BY result LIMIT #{rowSize} OFFSET #{startRow} /* 페이징 */
