mapper.update:
/*닉네임 변경*/
UPDATE MEMBER SET 
       nickname  = #{nickname}		    /* 새 닉네임 */
     , edit_date = current_TIMESTAMP	/* 수정일 */
 WHERE id_num    = #{idNum}

/*패스워드 변경*/
UPDATE MEMBER SET 
       password  = #{password}          /* 새 패스워드 */ 
     , edit_date = current_TIMESTAMP    /* 수정일 */
 WHERE id_num    = #{idNum}

/* 핸드폰 번호 변경 */
UPDATE MEMBER SET 
       phone     = #{phone}              /* 새 휴대폰 번호 */
     , edit_date = current_TIMESTAMP     /* 수정일 */
 WHERE id_num = #{idNum}

/* 공개,비공개 전환 */
UPDATE MEMBER SET
       locked = #{locked}   /* 공개 혹은 비공개 상태정보 */
 WHERE id_num = #{idNum}
